package net.ccbluex.liquidbounce.ui.client.clickgui.neverlose.Aboutimport net.ccbluex.liquidbounce.ui.client.neverlose.Implimport net.ccbluex.liquidbounce.ui.client.neverlose.Mainimport net.ccbluex.liquidbounce.ui.font.Fontsimport me.aquavit.liquidsense.utils.render.RenderUtilsimport net.ccbluex.liquidbounce.LiquidBounceimport java.awt.Colorimport java.text.SimpleDateFormatobject aboutMain : Main() {    fun drawabout(mouseX : Int , mouseY : Int , main : Main) {        if(main.hoverConfig(Impl.coordinateX + 410 , Impl.coordinateY + 14 , Impl.coordinateX + 420 , Impl.coordinateY + 26 , mouseX , mouseY , true)) {            Impl.openAbout = !Impl.openAbout        }        if(Impl.openAbout) {            when (Impl.hue) {                "white" -> {                    RenderUtils.drawNLRect(Impl.coordinateX + 470, Impl.coordinateY + 70, Impl.coordinateX + 630, Impl.coordinateY + 220, 2f,                            Color(255, 255, 255, 230).rgb)                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 84, Impl.coordinateX + 630, Impl.coordinateY + 85, Color(217,217,217).rgb) //分割线                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 179, Impl.coordinateX + 630, Impl.coordinateY + 180, Color(217,217,217).rgb) //分割线                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 124, Impl.coordinateX + 630, Impl.coordinateY + 125, Color(217,217,217).rgb) //分割线                    Fonts.bold25.drawCenteredString("LIQUIDSENSE.CC", Impl.coordinateX + 549, Impl.coordinateY + 100, Color(51,51,51).rgb, false)                    Fonts.font16.drawString("About LiquidSense", Impl.coordinateX + 485, Impl.coordinateY + 75, Color(8,8,8).rgb)                    Fonts.font16.drawString("Version:", Impl.coordinateX + 475, Impl.coordinateY + 130, Color(8,8,8).rgb)                    Fonts.font16.drawString("BuildType:", Impl.coordinateX + 475, Impl.coordinateY + 142, Color(8,8,8).rgb)                    Fonts.font16.drawString("Date:", Impl.coordinateX + 475, Impl.coordinateY + 154, Color(8,8,8).rgb)                    Fonts.font16.drawCenteredString("minger.club @2021", Impl.coordinateX + 548, Impl.coordinateY + 169, Color(8,8,8).rgb , false )                    Fonts.font16.drawString("Style", Impl.coordinateX + 475, Impl.coordinateY + 200, Color(105,105,105).rgb)                }                "black", "blue" -> {                    RenderUtils.drawNLRect(Impl.coordinateX + 470, Impl.coordinateY + 70, Impl.coordinateX + 630, Impl.coordinateY + 220, 2f,                            if (Impl.hue == "blue") Color(6, 16, 28, 245).rgb else Color(11, 11, 11, 230).rgb)                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 84, Impl.coordinateX + 630, Impl.coordinateY + 85,                            if (Impl.hue == "blue") Color(5,26,38).rgb else Color(27,29,31).rgb) //分割线                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 179, Impl.coordinateX + 630, Impl.coordinateY + 180,                            if (Impl.hue == "blue") Color(5,26,38).rgb else Color(27,29,31).rgb) //分割线                    RenderUtils.drawRect(Impl.coordinateX + 470, Impl.coordinateY + 124, Impl.coordinateX + 630, Impl.coordinateY + 125,                            if (Impl.hue == "blue") Color(5,26,38).rgb else Color(27,29,31).rgb) //分割线                    Fonts.bold25.drawCenteredString("LIQUIDSENSE.CC", Impl.coordinateX + 548, Impl.coordinateY + 100, Color(11, 160, 255).rgb, false)                    Fonts.bold25.drawCenteredString("LIQUIDSENSE.CC", Impl.coordinateX + 549, Impl.coordinateY + 100, Color(255, 255, 255).rgb, false)                    Fonts.font16.drawString("About LiquidSense", Impl.coordinateX + 485, Impl.coordinateY + 75, Color(255, 255, 255).rgb)                    Fonts.font16.drawString("Version:", Impl.coordinateX + 475, Impl.coordinateY + 130, Color(255, 255, 255).rgb)                    Fonts.font16.drawString("BuildType:", Impl.coordinateX + 475, Impl.coordinateY + 142, Color(255, 255, 255).rgb)                    Fonts.font16.drawString("Date:", Impl.coordinateX + 475, Impl.coordinateY + 154, Color(255, 255, 255).rgb)                    Fonts.font16.drawCenteredString("minger.club @2021", Impl.coordinateX + 548, Impl.coordinateY + 169, Color(255, 255, 255).rgb , false )                    Fonts.font16.drawString("Style", Impl.coordinateX + 475, Impl.coordinateY + 200, Color(133,155,168).rgb)                }            }            Fonts.csgo40.drawString("G", Impl.coordinateX + 472, Impl.coordinateY + 74, Color(11, 160, 255).rgb, false)            Fonts.font16.drawString("${LiquidBounce.CLIENT_VERSION}", Impl.coordinateX + 475 + Fonts.font16.getStringWidth("Version: "),                    Impl.coordinateY + 130, Color(24, 179, 246).rgb)            Fonts.font16.drawString(if (LiquidBounce.IN_DEV) "Dev" else "Release",                    Impl.coordinateX + 475 + Fonts.font16.getStringWidth("BuildType: "), Impl.coordinateY + 142,                    Color(24, 179, 246).rgb)            Fonts.font16.drawString(SimpleDateFormat("dd.MM.YY").format(System.currentTimeMillis())                    , Impl.coordinateX + 475 + Fonts.font16.getStringWidth("Date: "), Impl.coordinateY + 154,                    Color(24, 179, 246).rgb)            if (main.hoverConfig(Impl.coordinateX + 574, Impl.coordinateY + 200, Impl.coordinateX + 587, Impl.coordinateY + 210, mouseX, mouseY, true)) {                Impl.hue = "blue"            }            if (main.hoverConfig(Impl.coordinateX + 594, Impl.coordinateY + 200, Impl.coordinateX + 607, Impl.coordinateY + 210, mouseX, mouseY, true)) {                Impl.hue = "white"            }            if (main.hoverConfig(Impl.coordinateX + 614, Impl.coordinateY + 200, Impl.coordinateX + 627, Impl.coordinateY + 210, mouseX, mouseY, true)) {                Impl.hue = "black"            }            RenderUtils.drawNCircle(                    if (Impl.hue == "black") Impl.coordinateX + 620                    else if (Impl.hue == "white") Impl.coordinateX + 600                    else Impl.coordinateX + 580,                    Impl.coordinateY + 203, 5.0f,                    Color(24, 179, 246))            RenderUtils.drawFullCircle(Impl.coordinateX + 580, Impl.coordinateY + 203, 4f, 0f, Color(7,50,93))            RenderUtils.drawFullCircle(Impl.coordinateX + 600, Impl.coordinateY + 203, 4f, 0f, Color(255, 255, 255))            RenderUtils.drawFullCircle(Impl.coordinateX + 620, Impl.coordinateY + 203, 4f, 0f, Color(1, 1, 1))        }    } }