package net.ccbluex.liquidbounce.ui.client.neverloseimport net.ccbluex.liquidbounce.features.module.ModuleCategoryimport net.ccbluex.liquidbounce.ui.font.Fontsimport me.aquavit.liquidsense.utils.render.RenderUtilsimport net.minecraft.client.renderer.GlStateManagerimport java.awt.Colorclass Category(renderX : Float , renderY : Float , Me : ModuleCategory , expandCategory: String?) {    private val x = renderX    private val y = renderY    private val category = Me    private val expandname = expandCategory    fun drawCategory(mouseX : Int , mouseY : Int , gui : Main) {        if(Impl.selectedCategory == category.displayName && Impl.theType == Impl.Type.CLIENT)            RenderUtils.drawNLRect( Impl.coordinateX + x - 20.5f , Impl.coordinateY + y - 6.5f , Impl.coordinateX + x + 66.5f , Impl.coordinateY + y + 8.5f , 4f ,                    if (Impl.hue == "white") Color(214,214,214).rgb else if (Impl.hue == "black") Color(44,44,44).rgb else Color(5,53,76).rgb) //热点选项        when (expandname) {            "Combat" -> Fonts.font14.drawString(expandname ,Impl.coordinateX + 10 , Impl.coordinateY + y.toInt() - 32 ,                    if (Impl.hue == "white") Color(140,140,140).rgb else if (Impl.hue == "black") Color(70,70,70).rgb else Color(46,67,81).rgb)            "Utility" -> Fonts.font14.drawString(expandname ,Impl.coordinateX + 10 , Impl.coordinateY + y.toInt() - 105 ,                    if (Impl.hue == "white") Color(140,140,140).rgb else if (Impl.hue == "black") Color(70,70,70).rgb else Color(46,67,81).rgb)            "Miscellaneous" -> Fonts.font14.drawString(expandname ,Impl.coordinateX + 10 , Impl.coordinateY + y.toInt() - 50 ,                    if (Impl.hue == "white") Color(140,140,140).rgb else if (Impl.hue == "black") Color(70,70,70).rgb else Color(46,67,81).rgb)        }        GlStateManager.resetColor()        Fonts.font15.drawString(category.displayName, Impl.coordinateX + x.toInt(), Impl.coordinateY + y.toInt(),                if (Impl.hue == "blue" || Impl.hue == "black" ) -1 else Color(1,1,1).rgb)        when (category.displayName) {            "Blatant" -> Fonts.csgo40.drawString("B", Impl.coordinateX + x.toInt() - 14, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "Ghost" -> Fonts.csgo40.drawString("Y", Impl.coordinateX + x.toInt() - 13, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "Player" -> Fonts.csgo40.drawString("L", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt(), Color(2,169,245).rgb)            "Movement" -> Fonts.csgo40.drawString("W", Impl.coordinateX + x.toInt() - 13, Impl.coordinateY + y.toInt(), Color(2,169,245).rgb)            "Render" -> Fonts.csgo40.drawString("X", Impl.coordinateX + x.toInt() - 13, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "World" -> Fonts.csgo40.drawString("C", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "Exploit" -> Fonts.csgo40.drawString("D", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "Misc" -> Fonts.csgo40.drawString("H", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt() - 1, Color(2,169,245).rgb)            "Client" -> Fonts.csgo40.drawString("Q", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt() - 2, Color(2,169,245).rgb)            "Scripts" -> Fonts.csgo40.drawString("V", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt(), Color(2,169,245).rgb)            "Fun" -> Fonts.csgo40.drawString("U", Impl.coordinateX + x.toInt() - 15, Impl.coordinateY + y.toInt(), Color(2,169,245).rgb)        }        if(gui.hovertoFloatL(Impl.coordinateX + x - 22.5f , Impl.coordinateY + y - 5 , Impl.coordinateX + x + 72.5f , Impl.coordinateY + y + 15 ,mouseX, mouseY,true )) {            if(category.displayName != Impl.selectedCategory) {                Impl.theType = Impl.Type.CLIENT                Impl.selectedCategory = category.displayName                Impl.lwheel = 0f                Impl.rwheel = 0f            }        }    }}