package net.ccbluex.liquidbounce.ui.client.neverloseimport net.ccbluex.liquidbounce.features.module.ModuleCategoryimport net.ccbluex.liquidbounce.ui.font.Fontsimport me.aquavit.liquidsense.utils.render.RenderUtilsimport net.minecraft.client.renderer.GlStateManagerimport java.awt.Colorclass Category(renderX : Float , renderY : Float , Me : ModuleCategory , expandCategory: String?) {    private val x = renderX    private val y = renderY    private val category = Me    private val expandname = expandCategory    fun drawCategory(mouseX : Int , mouseY : Int , gui : Main) {        if(Impl.selectedCategory == category.displayName && Impl.theType == Impl.Type.CLIENT)            RenderUtils.drawNLRect( Impl.coordinateX + x - 20.5f , Impl.coordinateY + y - 6.5f , Impl.coordinateX + x + 66.5f , Impl.coordinateY + y + 8.5f , 4f , Color(5,53,76).rgb)        if(expandname != "")            Fonts.font14.drawString(expandname ,Impl.coordinateX + 10 , Impl.coordinateY + y.toInt() - 20 , Color(175 , 175 ,175).rgb )        GlStateManager.resetColor()        Fonts.font15.drawString(category.displayName, Impl.coordinateX + x.toInt(), Impl.coordinateY + y.toInt(), -1)        if(gui.hovertoFloatL(Impl.coordinateX + x - 22.5f , Impl.coordinateY + y - 5 , Impl.coordinateX + x + 72.5f , Impl.coordinateY + y + 15 ,mouseX, mouseY,true )) {            if(category.displayName != Impl.selectedCategory) {                Impl.theType = Impl.Type.CLIENT                Impl.selectedCategory = category.displayName                Impl.lwheel = 0f                Impl.rwheel = 0f            }        }    }}