package net.ccbluex.liquidbounce.ui.client.neverlose.implvalueimport me.aquavit.liquidsense.utils.render.RenderUtilsimport net.ccbluex.liquidbounce.ui.client.clickgui.neverlose.module.NModuleimport net.ccbluex.liquidbounce.ui.font.Fontsimport net.ccbluex.liquidbounce.value.BoolValueimport net.ccbluex.liquidbounce.value.Valueimport net.minecraft.client.renderer.GlStateManagerimport java.awt.Colorclass BooleanValuedraw(n: NModule, value: Value<*>) {    var nModule = n    var values = value as BoolValue    var yes = false    fun draw() {        if (nModule.main.hovertoFloatL(nModule.positionX + 150, nModule.positionY + 21 + nModule.valuePosY, nModule.positionX + 170f,                                       nModule.positionY + 33f + nModule.valuePosY, nModule.mouseX, nModule.mouseY, true) && yes) {            values.set(!values.get())        }        values.boolvalue.translate(if (values.get()) 10f else 0f, 0f)        RenderUtils.drawNLRect(nModule.positionX + 153, nModule.positionY + 27 + nModule.valuePosY, nModule.positionX + 167f,                                    nModule.positionY + 33f + nModule.valuePosY, 2.1f,                                    if (values.get()) Color(3, 23, 46).rgb else Color(7, 19, 31).rgb)        RenderUtils.drawFullCircle(nModule.positionX + 155 + values.boolvalue.x, nModule.positionY + 30 + nModule.valuePosY, 3.5f, 0f,                                   if (values.get()) Color(3, 168, 245) else Color(74, 87, 97))        GlStateManager.resetColor()        Fonts.font15.drawString(values.name, nModule.positionX + 6, nModule.positionY + 27 + nModule.valuePosY,                                if (values.get()) Color(255, 255, 255).rgb else Color(175, 175, 175).rgb)        nModule.valuePosY += nModule.module.openValue.y.toInt()    }}