package net.ccbluex.liquidbounce.ui.client.clickgui.neverlose.implvalueimport me.aquavit.liquidsense.utils.render.RenderUtilsimport net.ccbluex.liquidbounce.ui.client.clickgui.neverlose.module.NModuleimport net.ccbluex.liquidbounce.ui.font.Fontsimport net.ccbluex.liquidbounce.value.ListValueimport net.ccbluex.liquidbounce.value.Valueimport java.awt.Colorclass ListValuedraw(n: NModule, value: Value<*>) {    var nModule = n    var values = value as ListValue    var yes = false        fun draw() {        if (nModule.main.hovertoFloatL(nModule.positionX + 75f, nModule.positionY + 22f + nModule.valuePosY, nModule.positionX + 127f, nModule.positionY + 31f + nModule.valuePosY, nModule.mouseX, nModule.mouseY, true) && yes) {            values.openList = !values.openList        }        RenderUtils.drawRectBordered(nModule.positionX + 85.0, nModule.positionY + 21.0 + nModule.valuePosY, nModule.positionX + 170.0, nModule.positionY + 34.0 + nModule.valuePosY, 0.8, Color(7, 14, 22).rgb, Color(21, 31, 41, if (!values.openList) 100 else 0).rgb)        Fonts.font15.drawString(values.name, nModule.positionX + 6, nModule.positionY + 26 + nModule.valuePosY, Color(175, 175, 175).rgb)        nModule.main.drawText(values.get(), 99, Fonts.font16, nModule.positionX + 88, nModule.positionY + 26f + nModule.valuePosY, Color(175, 175, 175).rgb)        if (values.openList) {            RenderUtils.drawRect(nModule.positionX + 95f, nModule.positionY + 33f + nModule.valuePosY, nModule.positionX + 155f,                                 nModule.positionY + 35f + nModule.valuePosY, Color(7, 24, 34).rgb)        }        nModule.valuePosY += nModule.module.openValue.y.toInt()        for (valueof in values.values.filter { !it.startsWith(values.get()) }) {            if (values.openList) {                val hover = nModule.main.hovertoFloatL(nModule.positionX + 86f, nModule.positionY + 13.5f + nModule.valuePosY, nModule.positionX + 169,                                       nModule.positionY + 25f + nModule.valuePosY, nModule.mouseX, nModule.mouseY, false)                if (nModule.main.hovertoFloatL(nModule.positionX + 86f, nModule.positionY + 13.5f + nModule.valuePosY, nModule.positionX + 155f,                                               nModule.positionY + 25f + nModule.valuePosY, nModule.mouseX, nModule.mouseY, true) && yes) {                    values.set(valueof)                    values.openList = !values.openList                }                RenderUtils.drawRect(nModule.positionX + 86f, nModule.positionY + 13f + nModule.valuePosY, nModule.positionX + 169f,                                     nModule.positionY + 25f + nModule.valuePosY, if (hover) Color(10,22,34).rgb else Color(4,12,19).rgb)                nModule.main.drawText(valueof, 14, Fonts.font15, nModule.positionX + 88f, nModule.positionY + 17 + nModule.valuePosY,                                      if (hover) Color(255,255,255).rgb else Color(175, 175, 175).rgb)                nModule.valuePosY += 12                nModule.module.outvalue++            }        }    }}